mapper.select:
SELECT su.* FROM sys_user su JOIN sys_user_dept sud ON su.id = sud.user_id JOIN sys_dept_closure sdc ON sud.dept_id = sdc.ancestor_id JOIN sys_dept sd ON sud.dept_id = sd.id WHERE su.del_flag = 'F' AND sd.del_flag = 'F' AND sdc.descendant_id IS NOT NULL
AND sud.dept_id = #{deptId}
AND sud.dept_id IN ( SELECT descendant_id FROM sys_dept_closure WHERE ancestor_id = #{deptId} )
AND su.nickname LIKE CONCAT('%', #{nickname}, '%')
AND su.username = #{username}
AND su.phone = #{phone}
GROUP BY su.id ORDER BY su.create_time ASC

SELECT su.* FROM sys_user su LEFT JOIN sys_user_dept sud ON su.id = sud.user_id LEFT JOIN sys_dept_closure sdc ON sud.dept_id = sdc.ancestor_id LEFT JOIN sys_dept sd ON sud.dept_id = sd.id WHERE su.del_flag = 'F' AND (sdc.descendant_id IS NULL OR sd.del_flag = 'T')
AND su.nickname LIKE CONCAT('%', #{nickname}, '%')
AND su.username = #{username}
AND su.phone = #{phone}
GROUP BY su.id ORDER BY su.create_time ASC

SELECT * FROM sys_user su

    su.del_flag = 'F'
    AND su.nickname like concat('%', #{nickname}, '%')
    AND su.username = #{username}
    AND su.phone = #{phone}

ORDER BY su.create_time ASC
