mapper.select:
SELECT * FROM sys_user WHERE id IN ( SELECT su.id FROM sys_user su JOIN sys_user_dept sud ON su.id = sud.user_id JOIN sys_dept_closure sdc ON sud.dept_id = sdc.ancestor_id JOIN sys_dept sd ON sud.dept_id = sd.id
su.del_flag = 'F' AND sd.del_flag ='F' AND sdc.descendant_id IS NOT NULL
AND sud.dept_id = #{deptId}
AND sud.dept_id IN (SELECT descendant_id FROM sys_dept_closure WHERE ancestor_id = #{deptId})
AND su.nickname like concat('%', #{nickname}, '%') AND su.username = #{username} AND su.phone = #{phone}
GROUP BY su.id ORDER BY su.create_time ASC )

SELECT * FROM sys_user WHERE id IN ( SELECT su.id FROM sys_user su LEFT JOIN sys_user_dept sud ON su.id = sud.user_id LEFT JOIN sys_dept_closure sdc ON sud.dept_id = sdc.ancestor_id LEFT JOIN sys_dept sd ON sud.dept_id = sd.id
su.del_flag = 'F' AND ( sdc.descendant_id IS NULL OR sd.del_flag ='T' ) AND su.nickname like concat('%', #{nickname}, '%') AND su.username = #{username} AND su.phone = #{phone}
GROUP BY su.id ORDER BY su.create_time ASC )

SELECT * FROM sys_user su

    su.del_flag = 'F'
    AND su.nickname like concat('%', #{nickname}, '%')
    AND su.username = #{username}
    AND su.phone = #{phone}

ORDER BY su.create_time ASC

SELECT user_id, GROUP_CONCAT(dept_id ORDER BY dept_id) AS dept_ids, GROUP_CONCAT(CONCAT(dept_id, ':', dept_name) ORDER BY dept_id) AS dept_infos FROM (SELECT sud.user_id, sd.id AS dept_id, sd.name AS dept_name FROM sys_user_dept sud JOIN sys_dept sd ON sud.dept_id = sd.id WHERE sd.del_flag = "F" ) AS subquery WHERE user_id IN
#{userId}
GROUP BY user_id;

SELECT user_id, GROUP_CONCAT(role_id ORDER BY role_id) AS role_ids, GROUP_CONCAT(CONCAT( role_id, ':', role_name )) AS role_infos FROM ( SELECT sur.user_id, sur.role_id, sr.role_name FROM sys_user_role sur LEFT JOIN sys_role sr ON sur.role_id = sr.id WHERE sr.del_flag = "F" ) AS subquery WHERE user_id IN
#{userId}
GROUP BY user_id;
